import React,{ Component } from 'react';

class Search extends Component {

	state = {
		query: '',
	}

	handleInputChange = () => {
		this.setState({
			query: this.search.value
		})
	}

	render(){

		return(
				<div>
					<div class="search">
						<div class="searchProducts-l1" aria-owns="use-autocomplete-demo-popup" role="combobox" aria-expanded="true">
							<div class="default-button-l1 small">
							</div>
						</div>
					</div>

					<form>
					    <input
					      placeholder="Search for..."
					      ref={input => this.search = input}
					      onChange={this.handleInputChange}
					    />
					    <p>{this.state.query}</p>
					</form>
				</div>
		)
	}
}

export default Search;
